import React from 'react'
import AddTodoForm from "./AddTodoForm";
import TodoItem from "./TodoItem";
import { Container } from "react-bootstrap";
import '../css/AllTodoListItems.css'

class AllTodoListItems extends React.Component {

    constructor(props) {
        super(props)

        this.handler = this.handler.bind(this)
    }

    handler() {
        this.setState({

        })
    }

    state = {
        todoItems: []
    }

    componentDidMount() {
        fetch('http://localhost:8000/api/todo-all')
            .then(res => res.json())
            .then((data) => {
                this.setState({ todoItems: data })
            })
            .catch(console.log)
    }

    render() {
        return (
            <Container className='todoAppContainer'>
                <h1 className='todoTitle'>Todo!</h1>
                <hr/>
                <div>
                    <Container>
                        <AddTodoForm/>
                    </Container>
                    <hr/>
                    {this.state.todoItems.map((todoItem) => (
                            <TodoItem
                                key={todoItem.id}
                                title={todoItem.title}
                                details={todoItem.details}
                                completed={todoItem.completed}
                            />
                    ))}
                </div>
                <hr/>
            </Container>
        )
    }

}

export default AllTodoListItems
