import { Modal, Form, Button } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.css'
import { useState } from "react";
import axios from "axios";

function AddTodoForm( ) {
    const [show, setShow] = useState(false)

    const [formData, updateFormData] = useState(initialFormData)

    const handleChange = (e) => {
        updateFormData({
            formData,
            [e.target.name]: e.target.value.trim()
        })
        console.log(formData)
    }

    const onSubmit = (data) => {

        const sendFormat = {
            url: 'http://localhost:8000/api/todo-all/',
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json;charset=UTF=8'
            },
            data: {
                title: formData.title,
                details: formData.details,
                completed: formData.completed
            }

        }

        axios(sendFormat)
            .then(response => {
                console.log(response.status)
            })

    }

    return (
        <div>
            <Button variant='outline-primary' onClick={() => setShow(true)} block>Add</Button>

            <Modal
                show={show}
                onHide={() => setShow(false)}
                size='lg'
            >
                <Modal.Header closeButton>
                    <h2>Add Item</h2>
                </Modal.Header>
                <Modal.Body>
                    <Form onSubmit={handleSubmit(onSubmit)}>
                        <Form.Group controlId='titleForm'>
                            <Form.Label>Title</Form.Label>
                            <Form.Control name='title' placeholder='Do.... something?' onChange={handleChange}/>
                        </Form.Group>
                        <Form.Group controlId='descriptionForm'>
                            <Form.Label>Description</Form.Label>
                            <Form.Control name='details' as='textarea' rows='5' placeholder='Idk' onChange={handleChange}/>
                        </Form.Group>
                        <Form.Group controlId='completedForm'>
                            <Form.Check name='completed' label='Completed' onChange={handleChange}/>
                        </Form.Group>
                        <Button type='submit' variant='outline-primary' block>Add</Button>
                    </Form>
                </Modal.Body>

            </Modal>
        </div>

    )


}

export default AddTodoForm
