import { Modal, Form, Button } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.css'
import { useState } from "react";
import axios from "axios";

function AddTodoForm( ) {
    const [show, setShow] = useState(false)
    const [formData, updateFormData] = useState(initialFormData)

    function handleSubmit(data) {
        let title = this.refs.title
        let description = this.refs.description
        let completed = this.refs.completed

        const sendFormat = {
            url: 'https://localhost:8000/api/todo-all',
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json;charset=UTF=8'
            },
            data: {
                'title': title,
                'details': description,
                'completed': completed
            }

        }

        axios(sendFormat)
            .then(response => {
                console.log(response.status)
            })

    }

    return (
        <div>
            <Button variant='outline-primary' onClick={() => setShow(true)} block>Add</Button>

            <Modal
                show={show}
                onHide={() => setShow(false)}
                size='lg'
            >
                <Modal.Header closeButton>
                    <h2>Add Item</h2>
                </Modal.Header>
                <Modal.Body>
                    <Form onSubmit={this.handleSubmit} onChange={this.handleChange}>
                        <Form.Group controlId='titleForm'>
                            <Form.Label>Title</Form.Label>
                            <Form.Control  placeholder='Do.... something?'/>
                        </Form.Group>
                        <Form.Group controlId='descriptionForm'>
                            <Form.Label>Description</Form.Label>
                            <Form.Control  as='textarea' rows='5' placeholder='Idk'/>
                        </Form.Group>
                        <Form.Group controlId='completedForm'>
                            <Form.Check  label='Completed'/>
                        </Form.Group>
                        <Button type='submit' variant='outline-primary' block>Add</Button>
                    </Form>
                </Modal.Body>

            </Modal>
        </div>

    )


}

export default AddTodoForm
